/**
 * Credit to https://github.com/walker-tx/svelte5-tanstack-table-examples
 * for creating this solution to use TanStack Table v8 with Svelte 5.
 */
import { createTable, } from "@tanstack/table-core";
/**
 * Creates a reactive TanStack table object for Svelte.
 * @param options Table options to create the table with.
 * @returns A reactive table object.
 * @example
 * ```svelte
 * <script>
 *   const table = createSvelteTable({ ... })
 * </script>
 *
 * <table>
 *   <thead>
 *     {#each table.getHeaderGroups() as headerGroup}
 *       <tr>
 *         {#each headerGroup.headers as header}
 *           <th colspan={header.colSpan}>
 *         	   <FlexRender content={header.column.columnDef.header} context={header.getContext()} />
 *         	 </th>
 *         {/each}
 *       </tr>
 *     {/each}
 *   </thead>
 * 	 <!-- ... -->
 * </table>
 * ```
 */
export function createSvelteTable(options) {
    const resolvedOptions = mergeObjects({
        state: {},
        onStateChange() { },
        renderFallbackValue: null,
        mergeOptions: (defaultOptions, options) => {
            return mergeObjects(defaultOptions, options);
        },
    }, options);
    const table = createTable(resolvedOptions);
    let state = $state(table.initialState);
    function updateOptions() {
        table.setOptions((prev) => {
            return mergeObjects(prev, options, {
                state: mergeObjects(state, options.state || {}),
                // eslint-disable-next-line ts/no-explicit-any
                onStateChange: (updater) => {
                    if (updater instanceof Function)
                        state = updater(state);
                    else
                        state = mergeObjects(state, updater);
                    options.onStateChange?.(updater);
                },
            });
        });
    }
    updateOptions();
    $effect.pre(() => {
        updateOptions();
    });
    return table;
}
// eslint-disable-next-line ts/no-explicit-any
export function mergeObjects(...sources) {
    const target = {};
    for (let i = 0; i < sources.length; i++) {
        let source = sources[i];
        if (typeof source === "function")
            source = source();
        if (source) {
            const descriptors = Object.getOwnPropertyDescriptors(source);
            for (const key in descriptors) {
                if (key in target)
                    continue;
                Object.defineProperty(target, key, {
                    enumerable: true,
                    get() {
                        for (let i = sources.length - 1; i >= 0; i--) {
                            let s = sources[i];
                            if (typeof s === "function")
                                s = s();
                            const v = (s || {})[key];
                            if (v !== undefined)
                                return v;
                        }
                    },
                });
            }
        }
    }
    return target;
}

export { default as FlexRender } from "./flex-render.svelte";
export { renderComponent, renderSnippet } from "./render-component.js";
export { createSvelteTable } from "./data-table.svelte.js";

/**
 * Credit to https://github.com/walker-tx/svelte5-tanstack-table-examples
 * for creating this solution to use TanStack Table v8 with Svelte 5.
 */
/**
 * A helper class to make it easy to identify Svelte components in
 * `columnDef.cell` and `columnDef.header` properties.
 *
 * > NOTE: This class should only be used internally by the adapter. If you're
 * reading this and you don't know what this is for, you probably don't need it.
 *
 * @example
 * ```svelte
 * {@const result = content(context as any)}
 * {#if result instanceof RenderComponentConfig}
 *   {@const { component: Component, props } = result}
 *   <Component {...props} />
 * {/if}
 * ```
 */
export class RenderComponentConfig {
    component;
    props;
    constructor(component, props = {}) {
        this.component = component;
        this.props = props;
    }
}
/**
 * A helper class to make it easy to identify Svelte Snippets in `columnDef.cell` and `columnDef.header` properties.
 *
 * > NOTE: This class should only be used internally by the adapter. If you're
 * reading this and you don't know what this is for, you probably don't need it.
 *
 * @example
 * ```svelte
 * {@const result = content(context as any)}
 * {#if result instanceof RenderSnippetConfig}
 *   {@const { snippet, params } = result}
 *   {@render snippet(params)}
 * {/if}
 * ```
 */
export class RenderSnippetConfig {
    snippet;
    params;
    constructor(snippet, params) {
        this.snippet = snippet;
        this.params = params;
    }
}
/**
 * A helper function to help create cells from Svelte components through ColumnDef's `cell` and `header` properties.
 *
 * This is only to be used with Svelte Components - use `renderSnippet` for Svelte Snippets.
 *
 * @param component A Svelte component
 * @param props The props to pass to `component`
 * @returns A `RenderComponentConfig` object that helps svelte-table know how to render the header/cell component.
 * @example
 * ```ts
 * // +page.svelte
 * const defaultColumns = [
 *   columnHelper.accessor('name', {
 *     header: header => renderComponent(SortHeader, { label: 'Name', header }),
 *   }),
 *   columnHelper.accessor('state', {
 *     header: header => renderComponent(SortHeader, { label: 'State', header }),
 *   }),
 * ]
 * ```
 * @see {@link https://tanstack.com/table/latest/docs/guide/column-defs}
 */
export function renderComponent(component, props) {
    return new RenderComponentConfig(component, props);
}
/**
 * A helper function to help create cells from Svelte Snippets through ColumnDef's `cell` and `header` properties.
 *
 * The snippet must only take one parameter.
 *
 * This is only to be used with Snippets - use `renderComponent` for Svelte Components.
 *
 * @param snippet
 * @param params
 * @returns - A `RenderSnippetConfig` object that helps svelte-table know how to render the header/cell snippet.
 * @example
 * ```ts
 * // +page.svelte
 * const defaultColumns = [
 *   columnHelper.accessor('name', {
 *     cell: cell => renderSnippet(nameSnippet, { name: cell.row.name }),
 *   }),
 *   columnHelper.accessor('state', {
 *     cell: cell => renderSnippet(stateSnippet, { state: cell.row.state }),
 *   }),
 * ]
 * ```
 * @see {@link https://tanstack.com/table/latest/docs/guide/column-defs}
 */
export function renderSnippet(snippet, params) {
    return new RenderSnippetConfig(snippet, params);
}
